/**
 * healthCheck - checks how many hearts player has left and alerts them
 */
function healthCheck () 
{
    if (hearts == 3)
    {
        alert('You have 3 hearts left');
    }
    else if (hearts == 2)
    {
        alert('you have 2 heart left');
    }
    else if (hearts == 1)
    {
        alert('you have 1 heart left');
    }
}

